mapper.update:
UPDATE job_posting SET title=#{title}, job_exp=#{job_exp}, experience=#{experience}, education=#{education}, salary=#{salary}, position=#{position}, job_type=#{job_type}, hire_type=#{hire_type}, hire_num=#{hire_num}, preferred_tech=#{preferred_tech}, main_work=#{main_work}, com_addr=#{com_addr}, end_date=#{end_date}, job_posting_photo=#{job_posting_photo}, writeday=now()
,job_posting_photo=#{job_posting_photo}
WHERE num=#{num}

UPDATE user SET 

   com_liked=#{com_liked}


   com_liked=CONCAT(com_liked, ",", #{com_liked})

 WHERE username=#{username}

UPDATE user SET 

   job_scrap=#{job_scrap}


   job_scrap=CONCAT(job_scrap, ",", #{job_scrap})

 WHERE username=#{username}
